function updateFeature(a) {
    $("#featurepics img").removeClass("active").filter("." + a).addClass("active");
    $("#featurelist li").removeClass("active").filter("." + a).addClass("active");    
}
$("#featurelist li").on("click", function (b) {
    var a = $(b.target).attr("class");   
    if (a) {
        a = $.trim(a.replace("active", ""));
        updateFeature(a)
    }
});
$("#featurelist li p").on("click", function (b) {
    var a = $(b.target).parent().attr("class");
    if (a) {
        a = $.trim(a.replace("active", ""));
        updateFeature(a)
    }
});
$("#featurelist li h4").on("click", function (b) {
    var a = $(b.target).parent().attr("class");
    if (a) {
        a = $.trim(a.replace("active", ""));
        updateFeature(a)
    }
});
